    <section id="contact">
        <form action="./sendemail.inc.php" method="POST" id='footerForm' name='Form para envio de emails via modal'>
            <h3>Entre em contato</h3>
            <div class="loader loader-hidden"></div>
            <div class='fields'>

                <label>Nome ou empresa</label>
                <input type="text" name="name" class='txt-camp' required>

                <label>Email</label>
                <input type="email" name="email" class='txt-camp' required>

                <label>Telefone</label>
                <input type="tel" name="phone" class='txt-camp' maxlength="15" placeholder='(99) 99999-9999' onkeyup="handlePhone(event)" required>

                <label>Mensagem</label>
                <textarea placeholder="Digite uma mensagem para nossos desenvolvedores!" name="message" class='txt-camp'></textarea>

                <div class="submit-div">
                    <button type="submit" class="btn-submit" id="sendEmailButton">Enviar</button>
                </div>
            </div>

        </form>
    </section>

