    <script>
        // Modal Form
        document.addEventListener("DOMContentLoaded", () => {
            const sendEmailButton = document.getElementById("sendEmailButton");
            const loader = document.querySelector(".loader");
            const popup = document.querySelector(".popup");
            let emailSent = <?php echo isset($_GET['emailSent']) ? 'true' : 'false'; ?>;
            const popupContainer = document.querySelector('.popup-container');

            sendEmailButton.addEventListener("click", () => {
                loader.classList.remove("loader-hidden"); // Mostra o spinner

                // Simulando o processo de envio do email
                setTimeout(() => {
                    loader.classList.add("loader-hidden"); // Esconde o spinner
                    iniciaModal("modalID"); // Chama a função para fechar a modal e exibir o popup
                }, 6000);
            });

            // Verifica se o email foi enviado com sucesso na página anterior
            const queryParams = new URLSearchParams(window.location.search);
            emailSent = queryParams.get('emailSent');

            if (emailSent === 'true') {
                popup.style.display = 'block';
                popupContainer.style.display = 'block';
            } else {
                popupContainer.style.display = 'none'; // Corrigido aqui
            }

            // Fecha o popup quando o botão é clicado
            document.querySelector("#close-popup").addEventListener("click", () => {
                popupContainer.style.display = 'none';
                popup.style.display = "none";
                window.location.href = 'index.php?emailSent=false';

            });
        });
    </script>

